/*!
 * Scrollax v1.0
 * https://github.com/joashpereira/Scrollax.js
 */
(function ($) {
  "use strict";
  function Scrollax() {
    this.elements = [];
    this.init();
  }

  Scrollax.prototype.init = function () {
    var self = this;
    $(window).on("scroll", function () {
      self.render();
    });
  };

  Scrollax.prototype.render = function () {
    var scrollTop = $(window).scrollTop();
    this.elements.forEach(function (el) {
      var $el = el.el;
      var ratio = el.ratio;
      $el.css("transform", "translateY(" + scrollTop * ratio + "px)");
    });
  };

  Scrollax.prototype.add = function (selector, ratio) {
    this.elements.push({ el: $(selector), ratio: ratio });
  };

  window.Scrollax = Scrollax; 
})(jQuery);