/*!
 * Scrollax.js - Parallax scrolling with ease.
 * Author: Joash Pereira - http://joashpereira.com
 */
(function($) {
  $.Scrollax = function() {
    this.elements = [];
    this.win = $(window);
    this.init();
  };

  $.Scrollax.prototype = {
    init: function() {
      var self = this;
      self.win.on("scroll", function() {
        self.render();
      });
    },
    render: function() {
      var self = this;
      self.elements.forEach(function(e) {
        var scrollTop = self.win.scrollTop();
        e.el.css("transform", "translateY(" + (scrollTop * e.ratio) + "px)");
      });
    },
    add: function(selector, ratio) {
      this.elements.push({ el: $(selector), ratio: ratio });
    }
  };
})(jQuery);
